import { Component, OnInit } from '@angular/core';
import { MatDialog, MatDialogConfig } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { DodajSlikuComponent } from '../dodaj-sliku/dodaj-sliku.component';
import { KorisnikService } from '../korisnik.service';
import { Korisnik } from '../modeli/Korisnik';
import { Radionica } from '../modeli/Radionica';
import { Zahtev } from '../modeli/Zahtev';
import { RadionicaService } from '../radionica.service';
import { RegistracijaComponent } from '../registracija/registracija.component';
import { ZahtevService } from '../zahtev.service';

@Component({
  selector: 'app-korisnici',
  templateUrl: './korisnici.component.html',
  styleUrls: ['./korisnici.component.css']
})
export class KorisniciComponent implements OnInit {

  constructor(private kservis:KorisnikService,private ruter:Router,private dialog:MatDialog, private rservis:RadionicaService,private zservis:ZahtevService) { }

  ngOnInit(): void {
    this.korisnik=JSON.parse(localStorage.getItem('ulogovan'))  
    if(this.korisnik!=null) this.tip=this.korisnik.tip
    else this.tip="neregistrovan"  
    this.pret=false
    this.radionice=[]
    let naziv:string=JSON.parse(localStorage.getItem('pretraga_naziv'))
    let mesto:string=JSON.parse(localStorage.getItem('pretraga_mesto'))
    let rad:Radionica[]=[]
    this.kservis.dohvatiSveUcesnikeIOrganizatore().subscribe((k:Korisnik[])=>{
      this.svi_korisnici=k
    })
    this.rservis.dohvatiSve().subscribe((r:Radionica[])=>{
      rad=r
      if((naziv==null || naziv=="") && (mesto==null || mesto=="") ) {
        this.radionice=rad
        
      }
      else if((naziv!=null && naziv!="") && (mesto!=null && mesto!="")   ) rad.forEach((r)=>{
        if(r.naziv.toLowerCase().includes(naziv.toLowerCase()) && r.mesto.toLowerCase().includes(mesto.toLowerCase())) this.radionice.push(r)
      })
      else if(naziv!=null && naziv!="") {
        rad.forEach((r)=>{
        if(r.naziv.toLowerCase().includes(naziv.toLowerCase()) ) this.radionice.push(r)
      })
      }
      else if(mesto!=null && mesto!="") {
        rad.forEach((r)=>{
        if(r.mesto.toLowerCase().includes(mesto.toLowerCase())) this.radionice.push(r)
      })
      }
      if(this.radionice.length>0) this.pret=true
      localStorage.setItem('pretraga_naziv',null)
      localStorage.setItem('pretraga_mesto',null)
      
    }
 
    
    )
    this.zservis.dohvatiSve().subscribe((z:Zahtev[])=>{
      this.zahtevi=z
    })
    this.kservis.dohvatiSve().subscribe((k:Korisnik[])=>{
      k.forEach(kk=>{
        if(kk.id>this.sledId) this.sledId=kk.id
      })
      this.sledId++
    })
  }
  sledId=0
  id2:number
  ime:string
  prezime:string
  tip_2:String
  radionice_2:number[]
  prijavljene_radionice_2:number[]
  moje_radionice_2:number[]
  korisnicko_imee:string
  lozinkaa:string
  ime_2:string
  prezime_2:string
  korisnicko_imee_2:string
  lozinkaa_2:string
  telefon_2:string
  email_2:string
  naziv_organizacije_2:string
  adresa_organizacije_2:string
  id_organizacije_2:string
  slika_2:String
  korisnik:Korisnik
  organizator:boolean
  naziv_organizacije:string
  adresa_organizacije:string
  id_organizacije:string
  tip:String
  telefon:string
  email:string
  pret:boolean=false
  korisnicko_ime:String
  lozinka:String
  greska:String
  nema_korisnickog_imena:boolean
  nema_lozinke:boolean
  slika:string
  sirina:number
  visina:number
  radionice:Radionica[]
  pretrazene:Radionica[]=[]
  pretraga_mesto:String=null
  pretraga_naziv:String=null
  pretraga_izabrana:boolean=false
  svi_korisnici:Korisnik[]
  greskaa:string=null
  panelOpenState = false;
  zahtevi:Zahtev[]

  korisnici(){
    this.ruter.navigate(['korisnici'])
  }
  dodajSliku(){
    let dialogRef = this.dialog.open(DodajSlikuComponent);
   
    dialogRef.afterClosed().subscribe(() => {
      this.slika=dialogRef.componentInstance.url
      this.sirina=dialogRef.componentInstance.width
      this.visina=dialogRef.componentInstance.height
      if(this.visina>300 || this.sirina>300 || this.visina<100  || this.sirina<100) this.greskaa="Slika nije u odgovarajucem formatu!"
      else this.greskaa=null
    });
  }
  dodajSliku_2(){
    let dialogRef = this.dialog.open(DodajSlikuComponent);
   
    dialogRef.afterClosed().subscribe(() => {
      this.slika_2=dialogRef.componentInstance.url
      
    });
  }
  dodajKorisnika(){
    let tip=this.organizator?"organizator":"ucesnik"
    this.kservis.registracija(this.sledId,this.korisnicko_imee,this.lozinka,this.ime,this.prezime,tip,this.email,this.adresa_organizacije,this.naziv_organizacije,this.id_organizacije,this.telefon,this.slika).subscribe(r=>{
      window.location.reload()
    })
  }
  obrisiKorisnika(x){
    this.kservis.obrisi(x).subscribe(()=>{

    })
    window.location.reload()
  }
  rad(){
    this.ruter.navigate(['rad'])
  }
  azuriraj(x){
    this.kservis.promeniPodatke(this.id2,this.korisnicko_imee_2,this.lozinkaa_2,this.ime_2,this.prezime_2,this.tip_2,this.email_2,this.adresa_organizacije_2,this.naziv_organizacije_2,this.id_organizacije_2,this.telefon_2,this.slika_2).subscribe((res)=>{
      window.location.reload()
    })
    window.location.reload()

  }
  podesi(x:Korisnik){
    this.id2=x.id
    this.ime_2=x.ime
    this.prezime_2=x.prezime
    this.korisnicko_imee_2=x.korisnicko_ime
    this.telefon_2=x.telefon
    this.email_2=x.email
    this.lozinkaa_2=x.lozinka
    this.slika_2=x.slika
    this.tip_2=x.tip
    this.radionice_2=x.radionice
    this.moje_radionice_2=x.moje_radionice
    this.prijavljene_radionice_2=x.prijavljene_radionice
  }
  logo:String="data:image/jpeg;base64,"
  logovanje2(){
    this.greska=null
    this.nema_korisnickog_imena=false
    this.nema_lozinke=false
    if(this.lozinka==null || this.lozinka=="") this.nema_lozinke=true
    if(this.korisnicko_ime==null || this.korisnicko_ime=="") this.nema_korisnickog_imena=true
    if(this.nema_korisnickog_imena || this.nema_lozinke) {}
    else{
     
      this.kservis.logovanje(this.korisnicko_ime,this.lozinka).subscribe((k:Korisnik)=>{
        localStorage.setItem('ulogovan',JSON.stringify(k))
        if(k) {
          switch(k.tip){
            case 'ucesnik':this.ruter.navigate(['ucesnik'])
                          break
            case 'administrator':this.ruter.navigate(['admin'])
                          break
            case 'organizator':this.ruter.navigate(['organizator'])
                          break
          }
        }
        else this.greska="Unesite ispravne podatke za logovanje!"
      })
    }
  }
  logovanje(){
    this.ruter.navigate(['logovanje'])
  }
  registracija2(){
    const dialogConfig=new MatDialogConfig()
    dialogConfig.disableClose=true
    dialogConfig.autoFocus=true
    this.dialog.open(RegistracijaComponent)
    
  }
  registracija(){
    this.ruter.navigate(['registracija'])
  }
  pretraga(){
    if(this.pretraga_mesto!=null && this.pretraga_mesto!="") localStorage.setItem('pretraga_mesto',JSON.stringify(this.pretraga_mesto))
    if(this.pretraga_naziv!=null && this.pretraga_naziv!="") localStorage.setItem('pretraga_naziv',JSON.stringify(this.pretraga_naziv))
    window.location.reload()
  }
  sortiraj_prema_nazivu(){
    this.radionice.sort((r1,r2)=>{
      if(r1.naziv>r2.naziv) return 1
      if(r1.naziv==r2.naziv) return 0
      return -1
    })
  }
  sortiraj_prema_datumu(){
    this.radionice.sort((r1,r2)=>{
      if(r1.datum>r2.datum) return 1
      if(r1.datum==r2.datum) return 0
      return -1
    })
  }
  odjava(){
    localStorage.clear()
    window.location.reload()
  }
  promena_lozinke(){
    this.ruter.navigate(['promena_lozinke'])
  }
  moje_radionice(){
    this.ruter.navigate(['moje_radionice'])
  }
  profil(){
    this.ruter.navigate(['profil'])

  }
  prihvati(z:Zahtev){
    this.zservis.prihvati(z,this.sledId).subscribe(i=>{
      window.location.reload()
    })
  }
  odbij(z){
    this.zservis.odbij(z).subscribe(i=>{
      window.location.reload()
    })
  }
}
